// client/src/components/FileUpload.js
import React, { useState,useRef } from 'react';
import './FileUpload.css';
import axios from 'axios';
import uploadImage from './upload.png';
//import dotenv from 'dotenv';

//dotenv.config();

const FileUpload = ({ onUpload }) => {

  const [file, setFile] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const hiddenFileInput = useRef(null); // ADDED

  const handleClick = event => {
    hiddenFileInput.current.click();    // ADDED
  };

  const handleUpload = async () => {
    if (file) {
      // Read the file and encode it to base64
      const reader = new FileReader();
      reader.onload = async () => {
        const base64Data = reader.result.split(',')[1];
        console.log(base64Data);
        // Call the Google Cloud Vision API for OCR
        const apiKey = 'AIzaSyDomoRZT6ZWL6Z7-TOxf7hBPCqznRQOYUI';
        const apiUrl = 'https://vision.googleapis.com/v1/images:annotate?key=' + apiKey;

        const requestBody = {
          requests: [
            {
              image: {
                content: base64Data,
              },
              features: [
                {
                  type: 'DOCUMENT_TEXT_DETECTION',
                },
              ],
              imageContext: {
                languageHints: ['en', 'th'],
              },
            },
          ],
        };

        try {
          const response = await fetch(apiUrl, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(requestBody),
          });

          const result = await response.json();
          console.log('OCR Result:', result);

          // Extract OCR text from the result and call the onUpload function
          const ocrText = result.responses[0]?.fullTextAnnotation?.text || '';
          //onUpload(ocrText);
          fetchData(ocrText)
        } catch (error) {
          console.error('Error performing OCR with Google Cloud Vision API:', error);
        }
      };

      reader.readAsDataURL(file);
    }
  };
  const OPENAI_API_KEY = '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';
  const fetchData = async (ocrText) => {
    try {
      const response = await axios.post(
        'https://api.openai.com/v1/engines/davinci-codex/completions',
        {
          prompt: `OCRText`,
          max_tokens: 100,
        },
        {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${OPENAI_API_KEY}`,
          },
        }
      );
      onUpload(response.data.choices[0].text)
      //setResponse(response.data.choices[0].text);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  // const convertToJSON = async (ocrText) => {
  //   try {
  //     // Call OpenAI API to convert text to JSON
  //     const openaiAPIKey = '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'; // Replace with your OpenAI API key
  //     const openaiAPIUrl = 'https://api.openai.com/v1/chat/completions ';
  //     const prompt = `Convert the following text to JSON:\n\n${ocrText}`;

  //     const response = await axios.post(
  //       openaiAPIUrl,
  //       {
  //         prompt,
  //         max_tokens: 150,
  //       },
  //       {
  //         headers: {
  //           'Content-Type': 'application/json',
  //           Authorization: `Bearer ${openaiAPIKey}`,
  //         },
  //       }
  //     );

  //     // Extract JSON result from OpenAI response
  //     const jsonData = response.data.choices[0]?.text.trim();

  //     try {
  //       // Parse the JSON result
  //       const parsedData = JSON.parse(jsonData);

  //       // Log the parsed JSON data
  //       console.log('Parsed JSON Data:', parsedData);

  //       return parsedData;
  //     } catch (jsonError) {
  //       console.error('Error parsing JSON result from OpenAI:', jsonError);
  //       return null;
  //     }
  //   } catch (error) {
  //     console.error('Error calling OpenAI API:', error);
  //     return null;
  //   }
  // };


  return (
    <div class="upload-container">
      <div class="upload-btn-wrapper">
        <h3>Upload Your ID Card</h3>
      <button class="btn" onClick={handleClick}>
        <img class="image" src={uploadImage} alt="Upload a file"/>
      </button>
      </div>
      <input type="file" accept=".png, .jpg, .pdf" onChange={handleFileChange} class="file-input" ref={hiddenFileInput}/>
      <div class = "upload-btn-wrapper">
      <button class = "btn" onClick={handleUpload}>Submit</button>
      </div>
    </div>
  );
};


export default FileUpload;
