// client/src/components/RecordsTable.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RecordsTable = () => {
  const [records, setRecords] = useState([]);

  useEffect(() => {
    // Fetch records from the server when the component mounts
    fetchRecords();
  }, []); // Empty dependency array ensures the effect runs only once

  const fetchRecords = async () => {
    try {
      const response = await axios.get('http://localhost:5000/data');
      setRecords(response.data);
    } catch (error) {
      console.error('Error fetching records:', error);
    }
  };

  // Update the table when a new record is added
  useEffect(() => {
    const fetchData = async () => {
      await fetchRecords();
    };

    fetchData();
  }, []); // Empty dependency array ensures the effect runs only once

  return (
    <div>
      <h2>Records Table</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Last Name</th>
            <th>Date of Birth</th>
            <th>Date of Issue</th>
            <th>Date of Expiry</th>
            <th>Status</th>
            <th>Timestamp</th>
          </tr>
        </thead>
        <tbody>
          {records.map((record) => (
            <tr key={record._id}>
              <td>{record.identification_number}</td>
              <td>{record.name}</td>
              <td>{record.last_name}</td>
              <td>{record.date_of_birth}</td>
              <td>{record.date_of_issue}</td>
              <td>{record.date_of_expiry}</td>
              <td>{record.status}</td>
              <td>{record.timestamp}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default RecordsTable;
