// client/src/components/RecordsTable.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './RecordTable.css'; 

const RecordsTable = () => {
    const [records, setRecords] = useState([]);
    const [editMode, setEditMode] = useState(false);
  const [editedData, setEditedData] = useState({});
  const [editedId, setEditedId] = useState(null);

    // Fetch records from the server on component mount
    useEffect(() => {
      const fetchRecords = async () => {
        try {
          const response = await axios.get('http://localhost:5000/data');
          setRecords(response.data);
        } catch (error) {
          console.error('Error fetching records:', error);
        }
      };

      fetchRecords();
    }, []); // Empty dependency array to run this effect only once

    // Set up Server-Sent Events (SSE)
    useEffect(() => {
      const eventSource = new EventSource('http://localhost:5000/events');

      eventSource.onmessage = (event) => {
        const newRecord = JSON.parse(event.data);
        setRecords((prevRecords) => [newRecord, ...prevRecords]);
      };

      eventSource.onerror = (error) => {
        console.error('SSE error:', error);
        eventSource.close();
      };

      return () => {
        eventSource.close();
      };
    }, []);

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/data/${id}`);
            // Manually remove the deleted record from the state to update UI
            setRecords((prevRecords) => prevRecords.filter((record) => record._id !== id));
        } catch (error) {
          console.error('Error deleting record:', error);
        }
      };

      const handleEdit = (id) => {
        // Set edit mode to true and initialize edited data
        setEditMode(true);
        setEditedId(id);
        const recordToEdit = records.find((record) => record._id === id);
        setEditedData({
      identification_number: recordToEdit.identification_number,
      name: recordToEdit.name,
      last_name: recordToEdit.last_name,
      date_of_birth: recordToEdit.date_of_birth,
      date_of_issue: recordToEdit.date_of_issue,
      date_of_expiry: recordToEdit.date_of_expiry,
    });
      };

      // Handle apply edit
      const handleApplyEdit = async () => {
        try {
          await axios.put(`http://localhost:5000/data/${editedId}`, editedData);
          // Reset edit mode and clear edited data
          setEditMode(false);
          setEditedData({});
        } catch (error) {
          console.error('Error updating record:', error);
        }
      };

  return (
    <div>
      <h2>Records Table</h2>
      <ul className="records-list">
        {records.map((record, index) => (
          <li key={record._id} className="record-box">
            <div className="record-content">
            <strong>Record {index + 1}:</strong>
            <pre>
              {'{'}
              <br />
              {'    identification_number: ' + record.identification_number + ','}
              <br />
              <br />
              {'    name: ' + record.name + ','}
              <br />
              <br />
              {'    last_name: ' + record.last_name + ','}
              <br />
              <br />
              {'    date_of_birth: ' + record.date_of_birth + ','}
              <br />
              <br />
              {'    date_of_issue: ' + record.date_of_issue + ','}
              <br />
              <br />
              {'    date_of_expiry: ' + record.date_of_expiry + ','}
              <br />
              <br />
              {'    status: ' + record.status}
              <br />
              {'}'}
            </pre>

            {editMode && editedId === record._id && (
                <div className="edit-form">
                  {/* Render form inputs based on keys in the editedData */}
                  {Object.keys(editedData).map((key) => (
                    <div key={key}>
                      <label>{key}:</label>
                      <input
                        type="text"
                        value={editedData[key]}
                        onChange={(e) => setEditedData((prevData) => ({ ...prevData, [key]: e.target.value }))}
                      />
                    </div>
                  ))}
                  <button className="apply-button" onClick={handleApplyEdit}>
                    Apply
                  </button>
                </div>
              )}

            <button className="edit-button" onClick={() => handleEdit(record._id)}>
                Edit
              </button>
            <button className="delete-button" onClick={() => handleDelete(record._id)}>Delete</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default RecordsTable;
